mapper.select:
select * from iot_device_log_metadata


    AND iot_id=#{iotId}


    AND product_key=#{productKey}


    AND message_type=#{messageType}


    AND property=#{property}


    AND `event`=#{event}


    AND device_id=#{deviceId}


ORDER BY id DESC

mapper.delete:
DELETE
FROM iot_device_log_metadata
WHERE id < #{topId}
  AND iot_id = #{iotId}
  AND property = #{property};

DELETE
FROM iot_device_log_metadata
WHERE id < #{topId}
  AND iot_id = #{iotId}
  AND event = #{event};

mapper.select:
SELECT create_time
FROM iot_device_log_metadata
WHERE iot_id = #{iotId}
  AND message_type = 'EVENT'
  AND `event` = #{event}
ORDER BY id desc LIMIT 1
