INSERT INTO `protocol_file` (`file_path`, `file_content`) VALUES ('universal/api/TCP/单相导轨XA-GXSD8-4G.ms', '{\n  \"properties\" : { },\n  \"id\" : \"5393bfe75eb64c2182f4308458eadb5e\",\n  \"script\" : null,\n  \"groupId\" : \"f4beb08dc7fe4157b9f63a22a0e1a430\",\n  \"name\" : \"单相导轨XA-GXSD8-4G\",\n  \"createTime\" : 1752805008549,\n  \"updateTime\" : 1754787309090,\n  \"lock\" : null,\n  \"createBy\" : \"test\",\n  \"updateBy\" : \"test\",\n  \"path\" : \"abcxassxnanahsman\",\n  \"method\" : \"POST\",\n  \"parameters\" : [ ],\n  \"options\" : [ ],\n  \"requestBody\" : null,\n  \"headers\" : [ ],\n  \"paths\" : [ ],\n  \"responseBody\" : null,\n  \"description\" : \"这个是一个电表\",\n  \"requestBodyDefinition\" : null,\n  \"responseBodyDefinition\" : null\n}\n================================\n//16进制ASCII转STRING\nvar hex2str = stringhex => {\n\n    var i = 0\n    var asciidata = \"\"\n    while (i < stringhex.length()) {\n        asciidata += (String.valueOf(Integer.parseInt(stringhex.substring(i, i + 2), 16)) + \",\")\n        i += 2\n    }\n    asciidata = asciidata.substring(0, asciidata.length() - 1)\n    return asciiToString(asciidata)\n}\n//大小端转换\nvar reverseStr = data => {\n    var rData = \"\"\n    if (data.length() >= 0) {\n        let seq = data.length()\n        while (seq > 0) {\n            rData += data.substring(seq - 2, seq)\n            seq = seq - 2\n        }\n    }\n    return rData\n}\n//和校验计算\nvar check_CS = strData => {\n    let i = 0\n    let accum = 0\n    while (i < strData.length()) {\n        accum += Integer.parseInt(strData.substring(i, i + 2), 16)\n        i += 2\n    }\n    let l = Integer.toHexString(accum).length()\n    return Integer.toHexString(accum).substring(l - 2, l).toUpperCase()\n}\n//数据转换 倒转 -33\nvar transform = str => {\n    let i = 0\n    let result = \'\'\n    str = reverseTwoDigit(str)\n    while (i < str.length()) {\n        if (str.substring(i, 2 + i) == \'33\') {\n            i += 2\n            result += \'00\'\n        } else {\n            let value = Integer.parseInt(str.substring(i, 2 + i), 16) - 0x33;\n            i += 2\n            result += String.format(\'%02X\', value)\n        }\n    }\n    return Integer.parseInt(result, 10)\n}\n//+0x33方法\nvar dataplus = strData => {\n    let sData = \'\'\n    let i = 0\n    let pData = 0\n    while (i < strData.length()) {\n        pData = (Integer.parseInt(strData.substring(i, i + 2), 16) + 0x33) & 0b11111111\n        sData += String.format(\'%02X\', pData)\n        i += 2\n    }\n    return sData\n}\n\n//预解码，解析上报的设备编号产品deviceId\nvar preDecode = payload => {\n    var result = {}\n    //注册包\n    if (payload.length() == 12) {\n        result.deviceId = reverseTwoDigit(payload)\n    }\n    \n    //电表电量上报消息预解码\n    if(payload.length() == 98 && payload.substring(0,2).equals(\'68\')&& payload.substring(payload.length()-2,payload.length()).equals(\'16\')){\n        result.deviceId = reverseStr(payload.substring(2, 14))\n    }\n    return result\n}\n//上行处理\nvar decode = (payload,context) => {\n    var result = []\n    var propertiesObj = {\n        \"messageType\": \"PROPERTIES\",\n        \"properties\": {}\n    }\n    propertiesObj.properties.originalMessage = payload\n\n\n    //propertiesObj.properties.shadow = pData.shadow\n    result.push(propertiesObj)\n    //心跳包\n    if (payload.length() == 12) {\n        result.push(propertiesObj)\n        propertiesObj.tcpEncode = \'HEX\';\n        propertiesObj.replyPayload = \'7868\'\n    } else {\n        //抄表结果\n        if (payload.length() >= 94 && payload.substring(14, 18) == \'6891\') {\n            //表号\n            propertiesObj.properties.meterNo = reverseStr(payload.substring(2, 14))\n            var length = Integer.parseInt(payload.substring(18, 20), 16)\n\n            //总用电量\n            let mData = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            propertiesObj.properties.meterData = mData.substring(0, mData.length() - 1)\n            //取影子里的上次读数\n            // if (pData!=null &&pData.containsKey(\'shadow\')) {\n            //     if (pData.shadow.state.reported.containsKey(\'meterData\')) {\n            //         propertiesObj.properties.lastMeterData = pData.shadow.state.reported.meterData\n            //     }\n            // }\n            //propertiesObj.properties.meterData = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            //电价\n            propertiesObj.properties.meterPrice = (transform(payload.substring(36, 44)) * 0.0001).toFixed(4)\n            //电压\n            propertiesObj.properties.voltage = (transform(payload.substring(44, 52)) * 0.1).toFixed(1)\n            //电流\n            propertiesObj.properties.current = (transform(payload.substring(52, 60)) * 0.001).toFixed(3)\n            //功率\n            propertiesObj.properties.power = (transform(payload.substring(60, 68)) * 0.1).toFixed(1)\n            //购电次数\n            propertiesObj.properties.purchases = transform(payload.substring(68, 76))\n            //预警电量\n            propertiesObj.properties.warningPower = (transform(payload.substring(76, 84)) * 0.01).toFixed(2)\n            //剩余金额\n            propertiesObj.properties.remainingAmount = (transform(payload.substring(84, 92)) * 0.01).toFixed(2)\n            propertiesObj.properties.openState = (Integer.parseInt(payload.substring(92, 94), 16) - 0x33) == 27 ? \'1\' : \'0\'\n            //新增查询信号值，在reply里面下发\n            propertiesObj.tcpEncode = \'HEX\';\n            propertiesObj.replyPayload = \'727270632C676574637371\'\n        }\n        //抄表分包，第一部分\n        if (payload.length() == 64 && payload.substring(14, 18) == \'6891\') {\n            //表号\n            propertiesObj.properties.meterNo = reverseStr(payload.substring(2, 14))\n            var length = Integer.parseInt(payload.substring(18, 20), 16)\n\n            //总用电量\n            let mData = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            propertiesObj.properties.meterData = mData.substring(0, mData.length() - 1)\n            //取影子里的上次读数\n            // if (pData.containsKey(\'shadow\')) {\n            //     if (pData.shadow.state.reported.containsKey(\'meterData\')) {\n            //         propertiesObj.properties.lastMeterData = pData.shadow.state.reported.meterData\n            //     }\n            // }\n            //propertiesObj.properties.meterData = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            //电价\n            propertiesObj.properties.meterPrice = (transform(payload.substring(36, 44)) * 0.0001).toFixed(4)\n            //电压\n            propertiesObj.properties.voltage = (transform(payload.substring(44, 52)) * 0.1).toFixed(1)\n            //电流\n            propertiesObj.properties.current = (transform(payload.substring(52, 60)) * 0.001).toFixed(3)\n            //功率\n            propertiesObj.properties.power = (transform(payload.substring(60, 64)) * 0.1).toFixed(1)\n        }\n        //抄表分包，第二部分\n        if (payload.length() == 34) {\n            //\n            //购电次数\n            propertiesObj.properties.purchases = transform(payload.substring(4, 12))\n            //预警电量\n            propertiesObj.properties.warningPower = (transform(payload.substring(12, 20)) * 0.01).toFixed(2)\n            //剩余金额\n            propertiesObj.properties.remainingAmount = (transform(payload.substring(20, 28)) * 0.01).toFixed(2)\n            propertiesObj.properties.openState = (Integer.parseInt(payload.substring(28, 30), 16) - 0x33) == 27 ? \'1\' : \'0\'\n            //新增查询信号值，在reply里面下发\n            propertiesObj.tcpEncode = \'HEX\';\n            propertiesObj.replyPayload = \'727270632C676574637371\'\n        }\n        //控制开合闸回复的丢数据头68兼容处理\n        if (payload.length() == 48) {\n            if (payload.substring(12, 18).equals(\'689C0D\') && ((payload.substring(42, 44).equals(\'4D\')) || (payload.substring(42, 44).equals(\'4E\')))) {\n                payload = \'68\' + payload\n            }\n        }\n\n        //控制开合闸回复\n        if (payload.substring(14, 18) == \'689C\') {\n            let replyswitchObj = {\n                \"messageType\": \"REPLY\",\n                \"function\": \"switchOnOff\",\n                \"data\": {\n                    \"openState\": (Integer.parseInt(payload.substring(44, 46), 16) - 0x33) == 27 ? \'1\' : \'0\'\n                }\n            }\n\n            result.push(replyswitchObj)\n            //同步上报通电断电属性\n            propertiesObj.properties.openState = replyswitchObj.data.openState\n            //propertiesObj.properties.meterData = (transform(payload.substring(20, 28)) * 0.01).toFixed(2)\n            propertiesObj.properties.remainingAmount = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            propertiesObj.properties.purchases = transform(payload.substring(36, 44))\n            //新增查询信号值，在reply里面下发\n            propertiesObj.tcpEncode = \'HEX\';\n            propertiesObj.replyPayload = \'727270632C676574637371\'\n        }\n        //清表回复\n        if (payload.substring(14, 18) == \'689A\') {\n            let replyswitchObj = {\n                \"messageType\": \"REPLY\",\n                \"function\": \"reset\",\n                \"data\": {\n                    \"result\": \'设置成功\'\n                }\n            }\n            result.push(replyswitchObj)\n            let mData = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            propertiesObj.properties.meterData = mData.substring(0, mData.length() - 1)\n            //propertiesObj.properties.meterData = (transform(payload.substring(20, 28)) * 0.01).toFixed(2)\n            propertiesObj.properties.remainingAmount = (transform(payload.substring(28, 36)) * 0.01).toFixed(2)\n            propertiesObj.properties.purchases = transform(payload.substring(36, 44))\n            propertiesObj.properties.openState = (Integer.parseInt(payload.substring(44, 46), 16) - 0x33) == 27 ? \'1\' : \'0\'\n        }\n        //设置限值回复\n        if (payload.substring(14, 20) == \'68841C\') {\n            let replyLimitObj = {\n                \"messageType\": \"REPLY\",\n                \"function\": \"setOverloadLimit\",\n                \"data\": {\n                    \"overVoltageLimit\": \"\",\n                    \"overCurrrentLimit\": \"\"\n                }\n            }\n            result.push(replyLimitObj)\n            //囤积金额\n            //propertiesObj.properties.hoardAmount = \n\n        }\n        //售电回复\n        if (payload.substring(14, 20) == \'68840C\') {\n            let replyRechargeObj = {\n                \"messageType\": \"REPLY\",\n                \"function\": \"recharge\",\n                \"data\": {\n                    \"result\": \'设置成功\'\n                }\n            }\n            result.push(replyRechargeObj)\n            //囤积金额\n            //propertiesObj.properties.hoardAmount = \n\n        }\n        //比较上次读数和这次读数的差额\n        if (propertiesObj.properties.containsKey(\'lastMeterData\') && propertiesObj.properties.containsKey(\'meterData\')) {\n            let shortfallData = Float.parseFloat(propertiesObj.properties.meterData) - Float.parseFloat(propertiesObj.properties.lastMeterData)\n            propertiesObj.properties.shortfallData = Float.parseFloat(shortfallData.toFixed(2))\n            if (propertiesObj.properties.shortfallData < 0) {\n                let shortfallAlarmObj = {\n                    \"messageType\": \"EVENT\",\n                    \"event\": \"\",\n                    \"data\": {\n                        \"level\": \"warn\"\n                    }\n                }\n                shortfallAlarmObj.event = \'shortfallAlarm\'\n                shortfallAlarmObj.data.shortfallAlarm = \'读数倒流告警\'\n                shortfallAlarmObj.data.shortfallData = propertiesObj.properties.shortfallData\n                result.push(shortfallAlarmObj)\n            }\n        }\n        //CSQ数据，ICCID数据\n        /**\n         * rrpc,getcsq,22\n         * rrpc,geticcid,898604A8102440120152\n         * \n         */\n        if (payload.length() >= 8 && payload.substring(0, 8).equals(\'72727063\')) {\n            let rData = hex2str(payload)\n            let rArray = rData.split(\',\')\n            //CSQ数据\n            if (rArray[0].equals(\'rrpc\') && rArray[1].equals(\'getcsq\')) {\n                propertiesObj.properties.csq = rArray[2]\n                propertiesObj.properties.signalLevel = csq(propertiesObj.properties.csq)\n            }\n            if (rArray[0].equals(\'rrpc\') && rArray[1].equals(\'geticcid\')) {\n                propertiesObj.properties.iccid = rArray[2]\n            }\n\n        }\n    }\n    /*//开始字节\n    if (payload.substring(0, 2) != \'68\') {\n    	return jsonToStr(result)\n    }*/\n\n\n    return jsonToStr(result)\n}\n//下行处理\nvar encode = payload => {\n    var obj = toJson(payload)\n    //读取电表用量\n    if (obj.function == \'meterRead\') {\n        if (obj.data.meterNo.length() == 12) {\n            let str = \'68\' + reverseStr(obj.data.meterNo) + \'6811043335C433\'\n            let check = check_CS(str)\n            str = str + check + \'16\'\n            return str.toLowerCase()\n        }\n    }\n    //开关闸\n    if (obj.function == \'switchOnOff\') {\n        if (obj.data.meterNo.length() == 12) {\n            //1为合闸\n            let switchOn = obj.data.valveOn == \'1\' ? \'4E\' : \'4D\'\n            let str = \'68\' + reverseStr(obj.data.meterNo) + \'681C103533333334896745\' + switchOn + \'3347773B3A443C\'\n            let check = check_CS(str)\n            str = str + check + \'16\'\n            return str.toLowerCase()\n        }\n    }\n    //清表(清除所有，余额、总电量、购电次数、电价全部初始化)\n    if (obj.function == \'reset\') {\n        if (obj.data.meterNo.length() == 12) {\n            let str = \'68\' + reverseStr(obj.data.meterNo) + \'681A09353333333489674535\'\n            let check = check_CS(str)\n            str = str + check + \'16\'\n            return str.toLowerCase()\n        }\n    }\n    //超限值设置\n    if (obj.function == \'setOverloadLimit\') {\n        